const eslint = require('gulp-eslint');
const { srcPath } = require('./const');

function lint(gulp) {
    return async function lint() {
        return gulp
            .src(srcPath)
            .pipe(
                eslint({
                    configFile: '.eslintrc.json',
                    formatter: 'verbose',
                })
            )
            .pipe(eslint.format())
            .pipe(eslint.failAfterError());
    };
}

exports.lint = lint;
